$.post("./../logic/backend.php","function=getMyApplicationsIn10Days",function(o){json=JSON.parse(o);if(json)for(i=0;i<json.length;i++)$("ul.borrow_list").append('<li class="borrow_item" id="application_'+json[i].id+'"><header><h2>'+window.getRoomById(json[i].room).name+"</h2><h3>"+json[i].start+" - "+json[i].end+'</h3></header><p>状态：<span class="borrow_item_type">'+("0"==json[i].state?"有效":"1"==json[i].state?"已撤回":"已取消")+'</span></p><ul class="borrow_item_btn"><li><a href="#" onclick="revokeApplication('+json[i].id+')">申请取消</a></li></ul></li>');else alert("getApplicationsError")});function revokeApplication(i){$.post("./../logic/backend.php","function=revokeApplication&applicationId="+i,function(o){1==o?$("li.borrow_item#application_"+i).remove():alert(o)})}
