function getRoomById(e){$.ajax({method:"POST",url:"./../logic/backend.php",data:"function=getRoomById&roomId="+e,async:!1}).done(function(e){window.temp=JSON.parse(e)});return window.temp}function getAllRooms(){$.ajax({method:"POST",url:"./../logic/backend.php",data:"function=getAllRooms",async:!1}).done(function(e){window.temp=JSON.parse(e)});return window.temp}function getMe(){$.ajax({method:"POST",url:"./../logic/backend.php",data:"function=getMe",async:!1}).done(function(e){window.temp=JSON.parse(e)});return window.temp}function getUserById(e){$.ajax({method:"POST",url:"./../logic/backend.php",data:"function=getUserById&targetId="+e,async:!1}).done(function(e){window.temp=JSON.parse(e)});return window.temp}function getYmd(e){return(e=new Date(e.replace(/-/g,"/"))).getFullYear()+"-"+(e.getMonth()+1<10?"0":"")+(e.getMonth()+1)+"-"+(e.getDate()<10?"0":"")+e.getDate()}function getHis(e){return(e=new Date(e.replace(/-/g,"/"))).getHours()+":"+(e.getMinutes()<10?"0":"")+e.getMinutes()+":"+(e.getSeconds()<10?"0":"")+e.getSeconds()}
